using UnityEngine;

public class Sample : MonoBehaviour
{
    [SerializeField] private GameObject _button;
    [SerializeField] private GameObject _button1;

    private void Start()
    {
        //タプルでforeachが回せる
        foreach (var obj in (_button,_button1))
        {
            obj.SetActive(false);
        }
    }
}


